<?phpinclude 'defines.php';$post = (!empty($_POST)) ? true :false;if($post) {    $name = stripcslashes($_POST['name']);    $phone = stripcslashes($_POST['phone']);    $email = stripcslashes($_POST['email']);    $form = stripcslashes($_POST['type-form']);    $message = stripcslashes($_POST['message']);    $date = stripcslashes($_POST['date']);    $time = stripcslashes($_POST['time']);    switch ($form) {        case "show-real":            $typeForm = "Увидеть вживую";            break;        case "question":            $typeForm = "Задать вопрос";            break;        case "getCall":            $typeForm = "Заказать звонок";            break;        case "getOrder1":            $typeForm = "Заказать 1";            break;        case "getOrder2":            $typeForm = "Заказать 2";            break;        case "getOrder3":            $typeForm = "Заказать 3";            break;        case "getOrder4":            $typeForm = "Заказать 4";            break;        case "wantYes":            $typeForm = "Заказать строительство дома - ДА";            break;        case "wantCalc":            $typeForm = "Заказать строительство дома - Рассчитать стоимость";            break;        case "wantNo":            $typeForm = "Заказать строительство дома - Нет, еще подумаю";            break;    }    $subject = 'Заявка';    $error = '';    if(!empty($time) || !empty($date) || !empty($message)) {        $message = '            <html>                <head>                    <title>Заявка</title>                </head>                <body>                    <p>Имя: '.$name.'</p>                    <p>Email: '.$email.'</p>                    <p>Телефон: '.$phone.'</p>                    <p>Назва Формы: '.$typeForm.'</p>                    <p>Дата: '.$date.'</p>                    <p>Час: '.$time.'</p>                    <p>Сообщение: '.$message.'</p>                </body>            </html>';    } else {        $message = '            <html>                <head>                    <title>Заявка</title>                </head>                <body>                    <p>Имя: '.$name.'</p>                    <p>Email: '.$email.'</p>                    <p>Телефон: '.$phone.'</p>                    <p>Назва Формы: '.$typeForm.'</p>                </body>            </html>';    }    if(!$error) {        $mail = mail(CONTACT_FORM, $subject, $message,            "From: HouseBuild\r\n"            ."Content-type: text/html; charset=utf-8 \r\n"            ."X-Mailer: PHP/" . phpversion()        );        if($mail) {            echo "OK";        }    }    else {        echo '<div class="bg-danger">'.$error.'</div>';    }}